    cy.get('.items.radio-styled > .show-description').eq(freeList).should('contain', product.promotion.name);
    switch (product.promotion.productType) {
      case 'freePerProduct':
        // qty = 0
        qty = product.amount * product.promotion.qty;
        cy.get('.items.radio-styled > [align="center"]').eq(freeList).should('contain', qty);
        break;
    }
  }
});
